{
    // See https://go.microsoft.com/fwlink/?LinkId=733558
    // for the documentation about the tasks.json format
    "version": "2.0.0",
    "tasks": [
        {
            "label": "Setup All",
            "type": "shell",
            "options": {
                "cwd": "${workspaceFolder}"
            },
            "dependsOrder": "sequence",
            "dependsOn": [
                "Source requirements.txt",
                "Source setup.py",
                "Proto requirements.txt",
                "Proto setup.py",
                "Examples requirements.txt",
                "Examples setup.py"
            ],
            "command": "echo 'Finished Setup All'",
            "problemMatcher": []
        },
        {
            "label": "Source requirements.txt",
            "type": "shell",
            "options": {
                "cwd": "${workspaceFolder}/Source"
            },
            "command": "python3 -m pip install --upgrade pip  &&  python3 -m pip install -r requirements.txt"
        },
        {
            "label": "Examples requirements.txt",
            "type": "shell",
            "options": {
                "cwd": "${workspaceFolder}/Examples"
            },
            "command": "python3 -m pip install --upgrade pip  &&  python3 -m pip install -r requirements.txt"
        },
        {
            "label": "Proto requirements.txt",
            "type": "shell",
            "options": {
                "cwd": "${workspaceFolder}/Examples/proto"
            },
            "command": "python3 -m pip install --upgrade pip  &&  python3 -m pip install -r requirements.txt"
        },
        {
            "label": "Source setup.py",
            "type": "shell",
            "options": {
                "cwd": "${workspaceFolder}/Source"
            },
            "command": "python3 setup.py develop --user"
        },
        {
            "label": "Examples setup.py",
            "type": "shell",
            "options": {
                "cwd": "${workspaceFolder}/Examples"
            },
            "command": "python3 setup.py develop --user"
        },
        {
            "label": "Proto setup.py",
            "type": "shell",
            "options": {
                "cwd": "${workspaceFolder}/Examples/proto"
            },
            "command": "python3 setup_av.py develop --user && python3 setup_progeny.py develop --user"
        }
    ]
}

